module.exports = (req, res) => {
  if (req.body.firstName == null) {
    res.status(400).json({
      errors: ['missing required field first name']
    })
  } else if (req.body.lastName == null) {
    res.status(400).json({
      errors: ['missing required field last name']
    })
  } else {
    res.location('/').status(201).end()
  }
}
